# Input  una stringa
byteLength = input.encode('utf-8').__len__()

# Controlla se la lunghezza in bytes supera i 5000
if byteLength > 5000:
    return {
        "exceeds": True,
        "byteLength": byteLength,
        "message": "Il testo supera il limite di 5000 bytes"
    }
else:
    return {
        "exceeds": False,
        "byteLength": byteLength,
        "message": "Il testo  entro il limite di 5000 bytes"
    }
